#!/usr/bin/php<?phprequire_once __DIR__ . "/../inc/bootstrap.php";$sql = <<<SQLDROP TABLE IF EXISTS document;DROP TABLE IF EXISTS term;DROP TABLE IF EXISTS document_terms;CREATE TABLE document (    id INTEGER PRIMARY KEY AUTOINCREMENT,    title TEXT,    document TEXT);CREATE TABLE term (    id INTEGER PRIMARY KEY AUTOINCREMENT,    term TEXT);CREATE TABLE document_terms (    term_id INTEGER,    document_id INTEGER);SQL;$db->exec($sql);if ($db->errorCode()) {    var_dump($db->errorInfo());}